import React from "react";
import { useState } from "react";
import Icon from "./components/Icon";

//Toastify
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

//REactStrap
import { Button, Container, Col, Row, Card, CardBody } from "reactstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./App.css";

const itemsArray = new Array(9).fill("empty");

const App = () => {
	const [isCross, setisCross] = useState(false);
	const [winMessage, setWinMessage] = useState("");

	const reloadGame = () => {
		setisCross(false);
		setWinMessage("");
		itemsArray.fill("empty", 0, 9);
	};
	const checkIsWinner = () => {
		//
	};
	const changeitem = (itemNumber) => {
		if (winMessage) {
			return toast(winMessage, { type: "success" });
		}
		if (itemsArray[itemNumber] === "empty") {
			itemsArray[itemNumber] = isCross ? "cross" : "circle";
			setisCross(!isCross);
		} else {
			return toast("already filled", { type: "error" });
		}

		checkIsWinner();
	};

	return (
		<Container className="p-5">
			<ToastContainer position="bottom-center" />
			<Row>
				<Col md={6} className="offset-md-3">
					{winMessage ? (
						<div className="mb-2 mt-2">
							{" "}
							{/*margin-bottom  margin-top */}
							<h1 className="text-success text-uppercase text-center">
								{winMessage}
							</h1>
							{/* button to reset */}
							<Button color="success" block onClick={reloadGame}>
								Reload The Game
							</Button>
						</div>
					) : (
						<h1 className="text-center text-warning">
							{isCross ? "cross" : "Circle"} Turns
						</h1>
					)}
					<div className="grid">
						{itemsArray.map((item, index) => (
							<Card onClick={ () => changeitem(inde)}>
								<CardBody className="box">
									<Icon name={item} />
								</CardBody>
							</Card>
						))}
					</div>
				</Col>
			</Row>
		</Container>
	);
};

export default App;
